import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import jwtDecode from 'jwt-decode'; // Install this library using `npm install jwt-decode`

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Make a POST request to your backend's login endpoint
            const response = await axios.post('http://localhost:8080/api/v1/employee/login', {
                email,
                password,
            });

            // Extract the token from the response
            const token = response.data.token;

            // Store the token in localStorage
            localStorage.setItem('token', token);

            // Decode the JWT to extract useful information (e.g., email)
            const decodedToken = jwtDecode(token);
            console.log("Decoded Token:", decodedToken); // Debugging

            // If needed, you can store specific claims in localStorage
            localStorage.setItem('email', decodedToken.sub); // Store user's email

            // Navigate to the landing page on successful login
            navigate('/landing');
        } catch (err) {
            // Check if the error response status is 401
            if (err.response && err.response.status === 401) {
                setError('Invalid email or password.');
            } else {
                setError('An unexpected error occurred. Please try again.');
            }
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card">
                        <div className="card-body">
                            <h3 className="card-title text-center">Login</h3>
                            {error && <div className="alert alert-danger">{error}</div>}
                            <form onSubmit={handleSubmit}>
                                <div className="mb-3">
                                    <label htmlFor="email" className="form-label">Email</label>
                                    <input
                                        type="email"
                                        className="form-control"
                                        id="email"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="password" className="form-label">Password</label>
                                    <input
                                        type="password"
                                        className="form-control"
                                        id="password"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                        required
                                    />
                                </div>
                                <button type="submit" className="btn btn-primary w-100">Login</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LoginPage;
