import React, { useState, useEffect } from "react";
import axios from "axios";

const LandingPage = () => {
    const [specializations, setSpecializations] = useState([]);
    const [form, setForm] = useState({
        id: "",
        code: "",
        name: "",
        description: "",
        year: "",
        credits_required: ""
    });
    const [error, setError] = useState("");
    const [success, setSuccess] = useState("");

    const apiBaseUrl = "http://localhost:8080/api/v1/specialization";
    const token = localStorage.getItem("token"); // Assume the token is already stored on login

    // Fetch all specializations
    const fetchSpecializations = async () => {
        try {
            const response = await axios.get(apiBaseUrl, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setSpecializations(response.data);
        } catch (err) {
            console.error(err);
            setError("Failed to fetch specializations.");
        }
    };

    useEffect(() => {
        fetchSpecializations();
    }, []);

    // Handle input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setForm({ ...form, [name]: value });
    };

    // Create Specialization
    const createSpecialization = async () => {
        try {
            const response = await axios.post(apiBaseUrl, form, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setSuccess(response.data);
            fetchSpecializations(); // Refresh the list
        } catch (err) {
            console.error(err);
            setError("Failed to create specialization.");
        }
    };

    // Update Specialization
    const updateSpecialization = async () => {
        try {
            const response = await axios.put(`${apiBaseUrl}/${form.id}`, form, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setSuccess(response.data);
            fetchSpecializations(); // Refresh the list
        } catch (err) {
            console.error(err);
            setError("Failed to update specialization.");
        }
    };

    // Delete Specialization
    const deleteSpecialization = async (id) => {
        try {
            const response = await axios.delete(`${apiBaseUrl}/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setSuccess(response.data);
            fetchSpecializations(); // Refresh the list
        } catch (err) {
            console.error(err);
            setError("Failed to delete specialization.");
        }
    };

    return (
        <div className="container mt-5">
            <h1>Specialization Management</h1>
            {error && <div className="alert alert-danger">{error}</div>}
            {success && <div className="alert alert-success">{success}</div>}

            <div className="row">
                {/* Create / Update Specialization Form */}
                <div className="col-md-6">
                    <h3>{form.id ? "Update Specialization" : "Create Specialization"}</h3>
                    <form
                        onSubmit={(e) => {
                            e.preventDefault();
                            form.id ? updateSpecialization() : createSpecialization();
                        }}
                    >
                        <div className="mb-3">
                            <label htmlFor="id" className="form-label">Specialization ID</label>
                            <input
                                type="text"
                                className="form-control"
                                id="id"
                                name="id"
                                value={form.id}
                                onChange={handleInputChange}
                                placeholder="Optional for creation"
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="code" className="form-label">Code</label>
                            <input
                                type="text"
                                className="form-control"
                                id="code"
                                name="code"
                                value={form.code}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="name" className="form-label">Name</label>
                            <input
                                type="text"
                                className="form-control"
                                id="name"
                                name="name"
                                value={form.name}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="description" className="form-label">Description</label>
                            <textarea
                                className="form-control"
                                id="description"
                                name="description"
                                value={form.description}
                                onChange={handleInputChange}
                                required
                            ></textarea>
                        </div>
                        <div className="mb-3">
                            <label htmlFor="year" className="form-label">Year</label>
                            <input
                                type="text"
                                className="form-control"
                                id="year"
                                name="year"
                                value={form.year}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="credits_required" className="form-label">Credits Required</label>
                            <input
                                type="text"
                                className="form-control"
                                id="credits_required"
                                name="credits_required"
                                value={form.credits_required}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <button type="submit" className="btn btn-primary">
                            {form.id ? "Update" : "Create"}
                        </button>
                    </form>
                </div>

                {/* Display Specializations */}
                <div className="col-md-6">
                    <h3>Specializations</h3>
                    <ul className="list-group">
                        {specializations.map((specialization) => (
                            <li key={specialization.specialization_id} className="list-group-item">
                                <strong>{specialization.name}</strong> - {specialization.description}
                                <button
                                    className="btn btn-danger btn-sm float-end"
                                    onClick={() => deleteSpecialization(specialization.specialization_id)}
                                >
                                    Delete
                                </button>
                                <button
                                    className="btn btn-warning btn-sm float-end me-2"
                                    onClick={() => setForm(specialization)}
                                >
                                    Edit
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default LandingPage;
