import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const SpecializationDetails = () => {
  const { id } = useParams();
  const [data, setData] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSpecializationWithCourses = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(`http://localhost:8080/api/v1/specialization/${id}/courses`, {
          headers: { Authorization: `Bearer ${token}` },
        });
         console.log(response.data.courses.course_id);
        setData(response.data.courses)
      } catch (error) {
        setError("Error fetching specialization details.");
      } finally {
        setLoading(false);
      }
    };

    fetchSpecializationWithCourses();
  }, [id]);

  if (loading) {
    return <p className="text-center mt-4">Loading specialization details...</p>;
  }

  if (error) {
    return <div className="alert alert-danger">{error}</div>;
  }

  if (!data || !data.specialization) {
    return <p className="text-center mt-4">No specialization data found.</p>;
  }

  return (
    <div className="container mt-5">
      <h2 className="text-center">Specialization Details</h2>
      <div className="mt-4">
        <h4>Specialization: {data.specialization.name} ({data.specialization.code})</h4>
        <p><strong>Description:</strong> {data.specialization.description}</p>
        <p><strong>Credits Required:</strong> {data.specialization.credits_required}</p>
        <p><strong>Year:</strong> {data.specialization.year}</p>

        <h5 className="mt-4">Courses:</h5>
        {data.courses && data.courses.length > 0 ? (
          <table className="table table-bordered table-striped mt-3">
            <thead className="table-dark">
              <tr>
                <th>Course ID</th>
                <th>Code</th>
                <th>Name</th>
                <th>Description</th>
                <th>Credits</th>
              </tr>
            </thead>
            <tbody>
              {data.courses.map((course) => (

                <tr key={course.course_id}>
                  <td>{course.course_id}</td>
                  <td>{course.code}</td>
                  <td>{course.name}</td>
                  <td>{course.description}</td>
                  <td>{course.credits}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No courses available for this specialization.</p>
        )}
      </div>
    </div>
  );
};

export default SpecializationDetails;
