import React, { useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const AddCourses = () => {
  const { specializationId } = useParams(); // Access specialization ID from URL
  const [formData, setFormData] = useState({
    course_id: "",
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:8080/api/v1/specialization/courses",
        { specialization_id: specializationId, ...formData }, // Send only specialization_id and course_id
        { headers: { Authorization: `Bearer ${token}` } }
      );
      console.log("DATA2",response.data);
      setMessage("Course added successfully.");
    } catch (error) {
      setMessage("Error adding course.");
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">Add Course to Specialization</h2>
      {message && <div className="alert alert-info">{message}</div>}
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="course_id" className="form-label">Course ID</label>
          <input
            type="text"
            className="form-control"
            id="course_id"
            name="course_id"
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-success w-100">
          Add Course
        </button>
      </form>
    </div>
  );
};

export default AddCourses;
